export default class MovieApiClient {
  _apiBase = 'https://api.themoviedb.org'

  async getResource(url) {
    const apiKey =
      'eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI3Nzk4YjgwNWI2NmJjY2I1Y2U5NzMyNmIxZGUzYjFhMiIsInN1YiI6IjY1ZmIwZDgxYmYzMWYyMDE3ZWZkY2I2ZSIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.LVfwxMVRTW53I6hXx6qWvUqwHwOp2_ps86CvoYbCK4w'
    const apiUrl = `${this._apiBase}${url}`
    const res = await fetch(apiUrl, {
      method: 'GET',
      headers: {
        Authorization: `Bearer ${apiKey}`,
        Accept: 'application/json',
      },
    })

    // if (!res.ok) {
    //   throw new Error(`Could not fetch ${apiUrl}, received status: ${res.status}`)
    // }

    return await res.json()
  }

  async getGenres() {
    const res = await this.getResource('/3/genre/movie/list?language=en-US')
    return res.genres // This will return an array of genres with ids and names
  }

  async getSearchMovies(input, page) {
    const moviesRes = await this.getResource(
      `/3/search/movie?query=${input}&include_adult=false&language=en-US&page=${page}`
    )
    const genresRes = await this.getGenres()
    const genresMap = genresRes.reduce((acc, genre) => {
      acc[genre.id] = genre.name
      return acc
    }, {})
    const movies = moviesRes.results.map((movie) => ({
      id: movie.id,
      title: movie.title,
      overview: movie.overview
        ? movie.overview
        : 'ЗАГЛУШКА ЗАГЛУШКА ЗАГЛУШКА ЗАГЛУШКА ЗАГЛУШКА ЗАГЛУШКА ЗАГЛУШКА ЗАГЛУШКА ЗАГЛУШКА ЗАГЛУШКА ЗАГЛУШКА ЗАГЛУШКА ЗАГЛУШКА ЗАГЛУШКА ЗАГЛУШКА ЗАГЛУШКА ЗАГЛУШКА ЗАГЛУШКА ЗАГЛУШКА',
      releaseDate: movie.release_date,
      rating: movie.vote_average,
      posterPath: movie.poster_path
        ? `https://image.tmdb.org/t/p/w500${movie.poster_path}`
        : 'data:image/png;base64,', // Добавляем путь к постеру
      genreIds: movie.genre_ids, // Assuming the search results include genre_ids
      genres:
        movie.genre_ids.length !== 0
          ? movie.genre_ids.map((genreId) => genresMap[genreId])
          : ['  ЗАГЛУШКА ЗАГЛУШКА ЗАГЛУШКА ЗАГЛУШКА'], // Convert genre ids to names
    }))
    return {
      movies,
      totalResults: moviesRes.total_results,
    }
  }

  // https://api.themoviedb.org/3/movie/550?api_key=7798b805b66bccb5ce97326b1de3b1a2
  // 'https://api.themoviedb.org/3/search/movie?query=return&include_adult=false&language=en-US&page=1'
  // url = '/3/search/movie?query=return&include_adult=false&language=en-US&page=1'
}

